import { Component } from "react";

class product extends Component {
    state = { product:[{pid:0,ptitle:"",price:0,stock:0}],
    prod:{ptitle:"",price:0,stock:0} }

    componentDidMount(){
        this.getdata();
    }

    getdata=()=>{
        var helper=new XMLHttpRequest();
        helper.onreadystatechange=()=>{
            if(helper.status===200 && helper.readyState===4){
                var data=JSON.parse(helper.responseText);
                this.setState({product:data});
            }
        }
        helper.open("GET","http://127.0.0.2:5000/product/")
        helper.send();
    }
    addrec=()=>{
        var helper=new XMLHttpRequest();
        helper.onreadystatechange=()=>{
            if(helper.status===200 && helper.readyState===4){
                var data=JSON.parse(helper.responseText);

            }
        }
        helper.open("POST","http://127.0.0.2:5000/product/");
        helper.setRequestHeader("content-Type","application/json");
        helper.send(this.state.prod);
    }
    ontextchange=(args)=>{
        var pro={...this.state.prod};
        console.log(args.target.name);
        pro[args.target.name]=args.target.value;
        this.setState(pro);
    }

    render() { 
            return (<>
            <input type="text" name="ptitle" value={this.state.prod.ptitle} onChange={this.ontextchange}/>
        <input type="number" name="price" value={this.state.prod.price} onChange={this.ontextchange}/>
        <input type="number" name="stock" value={this.state.prod.stock} onChange={this.ontextchange}/>
        <br/>
        <input type="button" value="Add" onClick={this.addrec}/>
        <table border="1">
            <thead><tr>
            <th>Pid</th>
                <th>Ptitle</th>
                <th>Price</th>
                <th>Stock</th>
            </tr>

            </thead>
            <tbody>
                {this.state.product.map(ele=>{ 
                    return (<tr key={ele.pid}>
                        <td>{ele.pid}</td>
                        <td>{ele.ptitle}</td>
                        <td>{ele.price}</td>
                        <td>{ele.stock}</td>
                    </tr>)

                })}
            </tbody>
        </table>
        <p id="inputfield"></p></>);
    }
}

export default product;
