import { Component } from "react";
import product from "./product";
class Addrec extends Component {
    state = { product:{ptitle:"",price:0,stock:0} } 
    ontextchange=(args)=>{
        var prod={...this.state.product};
        prod[args.target.name]=args.target.value;
        this.setState(prod);
    }
    getdata=()=>{
        var helper=new XMLHttpRequest();
        helper.onreadystatechange=()=>{
            if(helper.status===200 && helper.readyState===4){
                var data=JSON.parse(helper.responseText);
                this.setState({product:data});
            }
        }
        helper.open("GET","http://127.0.0.2:5000/product/")
        helper.send();
    }
