import { Component } from "react";
import product from "./product";
class Delete extends Component {
    state = { deleteid:{pid:0} } 
    deletepro=()=>{

        var helper=new XMLHttpRequest();
        helper.onreadystatechange=()=>{
            if(helper.status==200 && helper.readyState==4){
                var data=helper.responseText;
                console.log()
                product.getData();
            }
        }
        helper.open("DELETE","http://127.0.0.2:5000/product/"+this.state.deleteid.pid);
        helper.setRequestHeader("content-Type","application/json");
        helper.send();
    }
    ontextchange=(args)=>{
        var id={...this.state.deleteid}
        id["pid"]=args.target.value;
        this.setState({deleteid:id})
    }
    render() { 
        return (<>
        <input type="text" value={this.state.deleteid.pid} onChange={this.ontextchange}/>
        <input type="button" value="delete" onClick={this.deletepro}/>
        </>);
    }
}

export default Delete;
